 <script type="text/javascript">



 var __json_b = {"a":null};



 var CLIENT_DEBUG_MODE = true;


 var DESGIN_ISDRAFT = false;

 jQuery(function () {
 jQuery.ajaxSetup({
 complete: function(xhr, textStatus) {

 if (xhr.status >= 200 && xhr.status < 300 && DESGIN_ISDRAFT === false) {
 if (this.url.indexOf('/Notification/Get') !== 0)
 {
 notificationIterationsCount = notificationIterationsCount +1;
 setCheckInterval(notificationIterationsCount);
 }
 }

 if (xhr.status === 301 && DESGIN_ISDRAFT === false) {
 default_redirect(xhr);
 }
 if (xhr.status === 500 && DESGIN_ISDRAFT === false)
 {

 if (typeof ajaxError == 'function')
 {
 ajaxError(xhr, textStatus);
 }
 else
 {
 default_ajaxError(xhr, textStatus);
 }
 }

 //Draft options
 if (xhr.status === 401 && DESGIN_ISDRAFT === true) {
 window.parent.sessionExpired();
 }

 if (xhr.status === 500 && DESGIN_ISDRAFT === true) {
 window.parent.design_ajaxError(textStatus);
 }

 }
 });
 });


 function default_ajaxError(xhr, textStatus) {

 console.error('AJAX ERROR: ' + textStatus + xhr.responseText);

 var errorMessage = 'Ha ocurrido un error. Por favor, intente nuevamente.';

 if (xhr.responseJSON != null && xhr.responseJSON.LogEventId != null) {
 errorMessage = errorMessage + '<br/> Identificación del error: <strong>' + xhr.responseJSON.LogEventId + '</strong>';
 }

 this.showNotification(errorMessage, 0);
 }

 function default_redirect(xhr, textStatus) {

 if (xhr.responseJSON != null && xhr.responseJSON.LoginRequired == true) {
 window.location = xhr.responseJSON.RedirectTo;
 }
 }

 var notificationIterationsCount = 0;
 function setCheckInterval(iteration)
 {
 setTimeout(function () {
 if (iteration === notificationIterationsCount){
 checkForNotifications();
 }
 }, 600);
 }


 function checkForNotifications()
 {
 notificationIterationsCount = 0;
 jQuery.ajax({
 url: '/Notification/Get',
 cache: false})
 .done(function (results){
 if (results != null && typeof(results) != 'undefined')
 {
 jQuery.each(results, function (i,o) {
 addNotification(o.Text, o.NotificationType);
 });

 if (results.length > 0){
 showNotifications();
 }
 }
 });
 }

 // fillHeight();

 jQuery(function (){
 setCheckInterval(0);
 });
 </script>






 <script>
 function MandingaHitter() {
 var gaIds = [];
 var fbId = null;

 var currency = 'ARS';
 var debug = false;

 var googleGtmInit = false;
 var googleCheckoutHitted = false;

 //helpers
 var isGoogleReady = function () {
 if (!(typeof gtag == 'undefined' || !gtag || !gaIds.length)) {
 return true;
 }

 console.error('MandingaHitter Warning: GoogleAnalytics is not ready.');

 return false;
 };

 var isFacebookReady = function () {
 if (!(typeof fbq == 'undefined' || !fbq || fbId == null)) {
 return true;
 }

 console.error('MandingaHitter Warning: FacebookPixel is not ready.');

 return false;
 };

 var initGtmGoogle = function (googleId) {
 if (googleGtmInit) return;

 googleGtmInit = true;

 if (!('dataLayer' in window)) {
 window.dataLayer = window.dataLayer || [];
 }

 window.dataLayer.push({
 'gtm.start': new Date().getTime(),
 event: 'gtm.js',
 });

 var script = document.createElement('script');

 script.async = true;
 script.src = 'https://www.googletagmanager.com/gtm.js?id=' + googleId;

 document.head.appendChild(script);

 if (debug) {
 console.log('Google GTM Init');
 }
 };

 //google hitters
 var pageViewToGoogle = function() {
 if (!isGoogleReady()) return;

 gaIds.forEach(function(googleId) {
 if (!/^UA-/.test(googleId)) {
 return;
 }

 var params = {
 send_to: googleId,
 };

 window.gtag('event', 'page_view', params);

 if (debug) {
 console.log('Hit -> Google PageView');
 console.log(params);
 }
 });
 };

 var viewItemToGoogle = function (payload) {
 if (!isGoogleReady()) return;

 gaIds.forEach(function (googleId) {
 var id = payload.id;
 var name = payload.name;
 var brand = payload.brand;
 var category = payload.category;
 var price = parseFloat(payload.price || 0);
 var quantity = parseInt(payload.quantity || 1);

 var params = {
 send_to: googleId,
 currency: currency,
 value: price,
 items: [
 {
 item_id: id,
 item_name: name,
 item_brand: brand,
 item_category: category,
 price: price,
 quantity: quantity,
 },
 ],
 };

 window.gtag('event', 'view_item', params);

 if (debug) {
 console.log('Hit -> Google ViewItem');
 console.log(params);
 }
 });
 };

 var addToCartToGoogle = function (payload) {
 if (!isGoogleReady()) return;

 gaIds.forEach(function (googleId) {
 var id = payload.id;
 var name = payload.name;
 var brand = payload.brand;
 var category = payload.category;
 var price = parseFloat(payload.price || 0);
 var quantity = parseInt(payload.quantity || 1);

 var params = {
 send_to: googleId,
 currency: currency,
 value: price,
 items: [
 {
 item_id: id,
 item_name: name,
 item_brand: brand,
 item_category: category,
 price: price,
 quantity: quantity,
 },
 ],
 };

 window.gtag('event', 'add_to_cart', params);

 if (debug) {
 console.log('Hit -> Google AddToCart');
 console.log(params);
 }
 });
 };

 var checkoutToGoogle = function (payload) {
 if (googleCheckoutHitted) return;

 if (!isGoogleReady()) return;

 googleCheckoutHitted = true;

 gaIds.forEach(function (googleId) {
 var items = [];

 if (payload.items) {
 payload.items.forEach(function (item) {
 items.push({
 item_id: item.id,
 item_name: item.name,
 item_brand: item.brand,
 item_category: item.category,
 price: parseFloat(item.price || 0),
 quantity: parseInt(item.quantity || 1),
 });
 });
 }

 var params = {
 send_to: googleId,
 currency: currency,
 value: payload.price,
 shipping: payload.shipping,
 items: items,
 };

 window.gtag('event', 'begin_checkout', params);

 if (debug) {
 console.log('Hit -> Google BeginCheckout');
 console.log(params);
 }
 });
 };

 var purchaseToGoogle = function (payload) {
 if (!isGoogleReady()) return;

 gaIds.forEach(function (googleId) {
 var items = [];

 if (payload.items) {
 payload.items.forEach(function (item) {
 items.push({
 item_id: item.id,
 item_name: item.name,
 item_brand: item.brand,
 item_category: item.category,
 price: parseFloat(item.price || 0),
 quantity: parseInt(item.quantity || 1),
 });
 });
 }

 var params = {
 send_to: googleId,
 transaction_id: payload.transaction_id,
 currency: currency,
 value: payload.price,
 shipping: payload.shipping,
 items: items,
 };

 window.gtag('event', 'purchase', params);

 if (debug) {
 console.log('Hit -> Google Purchase');
 console.log(params);
 }
 });
 };

 //public methods
 this.SetDebugMode = function (flag) {
 debug = flag;
 };

 this.SetCurrency = function (currencyName) {
 currency = currencyName;
 };

 this.GetCurrency = function () {
 return currency;
 };

 this.GoogleInit = function (googleId) {
 if (/^GTM-/.test(googleId)) {
 initGtmGoogle(googleId);
 return;
 }

 gaIds.push(googleId);

 window.gtag('config', googleId);
 };

 this.HitPageView = function () {
 pageViewToGoogle();
 };

 this.HitViewItem = function (payload) {
 viewItemToGoogle(payload);
 };

 this.HitAddToCart = function (payload) {
 addToCartToGoogle(payload);
 };

 this.HitCheckout = function (payload) {
 checkoutToGoogle(payload);
 };

 this.HitPurchase = function (payload) {
 purchaseToGoogle(payload);
 };

 this.MapToFacebookProductItem = function (item) {
 if (typeof item != 'object') return;

 return {
 id: item.id,
 quantity: parseInt(item.quantity || 0),
 name: item.name,
 };
 };
 }
 </script>
