 var card = $this.closest('#variantselector-container');

 var payload = {
 id: $this.attr('data-product_id'),
 price: $this.attr('data-product_price') || 0,
 quantity: card.find('.qs_select').val() || 1,
 name: card.attr('data-product_name'),
