 payload.shipping = $this.attr('data-shipping');

 TheMandingaHitter.HitCheckout(payload);

 if ('fbq' in window) {
 var params = {};

 params.contents = [];

 payload.items.forEach(function (item) {
 params.contents.push(
 TheMandingaHitter.MapToFacebookProductItem(item)
 );
 });

 params.content_type = 'product';
