 jQuery(document).on('click', '#Checkout .init-checkout-analytics', function () {
 var $this = jQuery(this);

 var payload = {};

 payload.items = [];

 jQuery('#Checkout .cdl-item_quantity-control').each(function () {
 var cartItemElement = jQuery(this);

 var cartItem = {
 id: cartItemElement.attr('data-product_id'),
 price: cartItemElement.attr('data-product_price') || 0,
 quantity: cartItemElement.attr('data-product_quantity') || 1,
 name: cartItemElement.attr('data-product_name'),
 brand: cartItemElement.attr('data-product_brand'),
 category: cartItemElement.attr('data-product_category'),
 };

 payload.items.push(cartItem);
 });

 payload.quantity = payload.items.length;
 payload.price = $this.attr('data-totalprice');
 payload.shipping = $this.attr('data-shipping');

 TheMandingaHitter.HitCheckout(payload);

 if ('fbq' in window) {
 var params = {};

 params.contents = [];

 payload.items.forEach(function (item) {
 params.contents.push(
 TheMandingaHitter.MapToFacebookProductItem(item)
 );
 });

 params.content_type = 'product';
 params.currency = TheMandingaHitter.GetCurrency();
 params.num_items = parseInt(payload.quantity || 1);
 params.value = parseFloat(payload.price);

 fbq('track', 'InitiateCheckout', params);
 }
 });
